import React, { Component } from "react";
import { Bar } from "react-chartjs-2";

// import useChart from "./useChart";
import { dataSet } from "./data";

export default function BarChart() {
  const canvasRef = React.useRef(null);
  const [data, setData] = React.useState(dataSet);
  useEffect(() => {
    setpercentage(80);

    setData({
      labels: ["Red", "Blue", "Yellow", "Green", "Purple", "Orange"],
      datasets: [
        {
          label: "# of Votes",
          data: [12, 19, 3, 5, 2, 3],
          backgroundColor: [
            "rgba(255, 99, 132, 0.2)",
            "rgba(54, 162, 235, 0.2)",
            "rgba(255, 206, 86, 0.2)",
            "rgba(75, 192, 192, 0.2)",
            "rgba(153, 102, 255, 0.2)",
            "rgba(255, 159, 64, 0.2)",
          ],
          borderColor: [
            "rgba(255, 99, 132, 1)",
            "rgba(54, 162, 235, 1)",
            "rgba(255, 206, 86, 1)",
            "rgba(75, 192, 192, 1)",
            "rgba(153, 102, 255, 1)",
            "rgba(255, 159, 64, 1)",
          ],
          borderWidth: 1,
        },
      ],
    });
  }, [setpercentage, setData]);

  const useChart = new Chart(canvasRef, {
    type: "line",

    data: {
      labels: Object.keys(data).filter((key) => data[key].isVisible),

      datasets: [
        {
          label: "# of Votes",

          data: Object.keys(data)
            .filter((key) => data[key].isVisible)
            .map((key) => data[key].value),

          backgroundColor: Object.keys(data)
            .filter((key) => data[key].isVisible)
            .map((key) => data[key].bgColor),

          borderColor: Object.keys(data)
            .filter((key) => data[key].isVisible)
            .map((key) => data[key].borderColor),

          borderWidth: 1,
        },
      ],
    },
    options: {
      legend: {
        display: true,
        labels: {
          fontColor: "#ff0000",
        },
      },
      scales: {
        yAxes: [
          {
            ticks: {
              beginAtZero: true,
            },
          },
        ],
      },
    },
  });
  return (
    <div>
      <canvas ref={canvasRef} width="400" height="200" />
    </div>
  );
}
